import generateId from './generateId.js';
import capitalizedString from './capitalizedString.js';
import formatForDate from './formatForDate.js';
import getAge from './getAge.js';

const getUserData  = data => {
  const arrayName = data.name.split(' ');

  return {
    id: generateId(),
    firstName: capitalizedString(arrayName[0]),
    lastName: arrayName[1] && capitalizedString(arrayName[1]),
    dateBirth: formatForDate(data.dateBirth, true),
    age: getAge(data.dateBirth),
    purpose: capitalizedString(data.purpose),
  };
};

export default getUserData;
